ALTER FUNCTION CHECK_CUSTOMER_SERVICE (
    CUSTOMER_ID TYPE OF UID,
    FOR_SRV D_UID_NULL,
    FOR_DATE D_DATE = current_date)
RETURNS D_INTEGER
AS
DECLARE SERVICE_ID TYPE OF UID;
DECLARE STATE D_INTEGER;
DECLARE STATE_DATE D_DATE;
DECLARE STATE_SRV TYPE OF UID;
DECLARE result D_INTEGER;
begin
  FOR_DATE = coalesce(FOR_DATE, current_date);
  result = null;
  select
      ss.Serv_Id
    , ss.State_Sgn
    , ss.State_Date
    , ss.State_Srv
    from subscr_serv ss
    where ss.Customer_Id = :CUSTOMER_ID
          and ss.Serv_Id = :FOR_SRV
  into :SERVICE_ID, :STATE, :STATE_DATE, :STATE_SRV;

  if (STATE_DATE < FOR_DATE) then begin
    if (STATE = 1) then
      result = 1;
    else
    if (STATE_SRV = -3) then
      result = -1;
    else
      result = 0;
  end
  else begin -- если будущим числом, то проверим в другой таблице
    if (exists(select
                   customer_id
                 from Subscr_Hist h
                 where h.Customer_Id = :CUSTOMER_ID
                       and h.Serv_Id = :SERVICE_ID
                       and :FOR_DATE between h.Date_From and h.Date_To)) then
      result = 1;
    else begin
      -- нужно еще допзапросы делать
      STATE_DATE = null;
      select
          max(h.Date_To)
        from Subscr_Hist h
        where h.Customer_Id = :CUSTOMER_ID
              and h.Serv_Id = :SERVICE_ID
              and h.Date_To <= :FOR_DATE
      into :STATE_DATE;
      if (STATE_DATE is null) then
        result = 0;
      else begin
        STATE_SRV = null;
        select
            h.Disact_Serv_Id
          from Subscr_Hist h
          where h.Customer_Id = :CUSTOMER_ID
                and h.Serv_Id = :SERVICE_ID
                and h.Date_To = :STATE_DATE
        into :STATE_SRV;
        if (STATE_SRV = -3) then
          result = -1;
      end
    end
  end

  return result;
end^


ALTER FUNCTION DISTANCE (
    LNG1 D_GEOPOINT,
    LAT1 D_GEOPOINT,
    LNG2 D_GEOPOINT,
    LAT2 D_GEOPOINT)
RETURNS D_INTEGER DETERMINISTIC
AS
DECLARE variable lat_1 FLOAT DEFAULT 0.0;
DECLARE variable lat_2 FLOAT DEFAULT 0.0;
DECLARE variable lng_1 FLOAT DEFAULT 0.0;
DECLARE variable lng_2 FLOAT DEFAULT 0.0;
DECLARE variable cl1 FLOAT DEFAULT 0.0;
DECLARE variable cl2 FLOAT DEFAULT 0.0;
DECLARE variable sl1 FLOAT DEFAULT 0.0;
DECLARE variable sl2 FLOAT DEFAULT 0.0;
DECLARE variable delta FLOAT DEFAULT 0.0;
DECLARE variable cdelta FLOAT DEFAULT 0.0;
DECLARE variable sdelta FLOAT DEFAULT 0.0;
DECLARE variable x FLOAT DEFAULT 0.0;
DECLARE variable y FLOAT DEFAULT 0.0;
DECLARE variable ad FLOAT DEFAULT 0.0;
DECLARE variable dist D_INTEGER DEFAULT 0;
DECLARE variable EARTH_RAD D_INTEGER DEFAULT 0;

BEGIN
    EARTH_RAD = 6372795;
    lat_1 = lat1 * PI() / 180.0;
    lat_2 = lat2 * PI() / 180.0;
    lng_1 = lng1 * PI() / 180.0;
    lng_2 = lng2 * PI() / 180.0;
    cl1 = COS(lat_1);
    cl2 = COS(lat_2);
    sl1 = SIN(lat_1);
    sl2 = SIN(lat_2);

    delta = lng_2 - lng_1;
    cdelta = COS(delta);
    sdelta = SIN(delta);

    y = SQRT(POWER(cl2 * sdelta, 2) + POWER(cl1 * sl2 - sl1 * cl2 * cdelta, 2));
    x = sl1 * sl2 + cl1 * cl2 * cdelta;

    ad = ATAN2(y, x);
    dist = ad * EARTH_RAD;
    RETURN dist;
END^


ALTER FUNCTION ESCAPE_STRING (
    ASTRING D_VARCHAR8000)
RETURNS D_VARCHAR8000
AS
begin
  AString = replace(AString, '\', '\\');
  AString = replace(AString, ascii_char(0x08), '\b');
  AString = replace(AString, ascii_char(0x09), '\t');
  AString = replace(AString, ascii_char(0x0A), '\r');
  AString = replace(AString, ascii_char(0x0C), '\f');
  AString = replace(AString, ascii_char(0x0D), '\n');
  AString = replace(AString, '"', '\"');
  AString = replace(AString, '/', '\/');
  return AString;
end^


ALTER FUNCTION FORMAT_CURRENCY (
    CURR D_N15_2,
    TS D_CHAR1 = ' ',
    DS D_CHAR1 = ',')
RETURNS D_VARCHAR50 DETERMINISTIC
AS
declare variable vTh  D_INTEGER;
declare variable vP   D_INTEGER;
declare variable vRes D_Varchar50;
begin
  TS = coalesce(TS, '');
  DS = coalesce(DS, '');

  vP = mod(round(Curr * 100, 0), 100);
  vTh = round(Curr, 0);
  vRes = DS || lpad(vP, 2, '0');
  while (vTh <> 0) do begin
    vP = mod(vTh, 1000);
    vTh = round(vTh / 1000, 0);
    vRes = ts || lpad(vP, 3, '0') || vRes;
  end

  return trim(leading '0' from trim(leading ts from vRes));
end^


ALTER FUNCTION GET_FREE_IP_BY_MASK (
    IP_MASK D_IP,
    SKIP_IPS D_VARCHAR1000 = '')
RETURNS D_IP
AS
declare variable MIN_IP        D_INT_IP;
declare variable CUR_IP        D_INT_IP;
declare variable I             D_Integer;
declare variable e             D_Integer;
declare variable REPITS        D_Integer;
declare variable IP            D_Ip;
declare variable Ip1           D_Ip;
declare variable Ip2           D_Ip;
declare variable Ip3           D_Ip;
declare variable FIRST_MASK_IP D_Ip;
declare variable S_SKP         D_Integer;
declare variable E_SKP         D_Integer;
begin
  IP_MASK = coalesce(trim(IP_MASK), '');
  IP = '';
  FIRST_MASK_IP = '100.0.0.0';
  REPITS = 0;
  SKIP_IPS = coalesce('|' || SKIP_IPS || '|', '');

  select
      Var_Value
    from Settings
    where var_Name = 'IP_RESERV_START'
  into :S_SKP;
  S_SKP = coalesce(S_SKP, 0);

  select
      Var_Value
    from Settings
    where var_Name = 'IP_RESERV_END'
  into :E_SKP;
  E_SKP = coalesce(E_SKP, 0);

  if (IP_MASK <> '') then begin
    -- удалим из маски 1.2.3.4 4 разряд
    i = char_length(IP_MASK) - char_length(replace(IP_MASK, '.', ''));
    if (i = 3) then begin
      i = position('.' in IP_MASK);
      ip1 = left(IP_MASK, i - 1);
      IP_MASK = substring(IP_MASK from i + 1);
      i = position('.' in IP_MASK);
      ip2 = left(IP_MASK, i - 1);
      IP_MASK = substring(IP_MASK from i + 1);
      i = position('.' in IP_MASK);
      ip3 = left(IP_MASK, i - 1);

      FIRST_MASK_IP = ip1 || '.' || ip2 || '.' || ip3 || '.1';
      REPITS = 255;
    end
    else
    if (i = 1) then begin
      i = position('.' in IP_MASK);
      ip1 = left(IP_MASK, i - 1);
      IP_MASK = substring(IP_MASK from i + 1);
      ip2 = right(IP_MASK, i + 1);
      if (ip2 = '') then
        ip2 = '0';

      FIRST_MASK_IP = ip1 || '.' || ip2 || '.0.1';
      REPITS = 255 * 255 * 255;
    end
    else
    if (i = 2) then begin
      i = position('.' in IP_MASK);
      ip1 = left(IP_MASK, i - 1);
      IP_MASK = substring(IP_MASK from i + 1);
      i = position('.' in IP_MASK);
      ip2 = left(IP_MASK, i - 1);
      IP_MASK = substring(IP_MASK from i + 1);
      ip3 = right(IP_MASK, i + 1);
      if (ip3 = '') then
        ip3 = '0';

      FIRST_MASK_IP = ip1 || '.' || ip2 || '.' || ip3 || '.1';
      REPITS = 255 * 255;
    end
  end

  i = 0;
  MIN_IP = INET_ATON(FIRST_MASK_IP);
  while (I < REPITS) do begin
    e = null;
    CUR_IP = MIN_IP + i;
    IP = INET_NTOA(CUR_IP);
    -- нужно ли пропускать адреса в начале и конце
    CUR_IP = bin_and(CUR_IP, 255);
    if ((CUR_IP <= S_SKP)
        or
        (CUR_IP >= (255 - E_SKP))) then
      e = -2;

    -- проверим, нужно ли пропускать ip
    if ((not e is null)
        or
        ((skip_ips <> '') and (position('|' || IP || '|', skip_ips) > 0))) then
      e = -1;
    else
