ALTER FUNCTION ONLY_DIGITS (
    A_VALUE D_VARCHAR255)
RETURNS D_VARCHAR255 DETERMINISTIC
AS
declare variable i d_integer;
declare variable j d_integer;
declare variable d d_char1;
declare variable DIGITS D_VARCHAR255;
begin

  j = CHAR_LENGTH(a_value);
  i = 1;
  DIGITS = '';
  while (i <= j) do begin
    d = substring(a_value FROM i FOR 1);
    IF (d BETWEEN '0' AND '9')
    then DIGITS = DIGITS ||d;
    i = i + 1;
  end

  return DIGITS;
end^


ALTER FUNCTION WHERE_IS_IP (
    IP D_IP)
RETURNS INTEGER
AS
declare variable IP_WHERE D_INTEGER;
begin
  IP_WHERE = null;
  if (coalesce(IP, '') = '') then
    IP_WHERE = -1;
  else begin
    select first 1
        1
      from TV_LAN T
      where IP = :IP
    into :IP_WHERE;

    if (IP_WHERE is null) then begin
      select first 1
          0
        from EQUIPMENT E
        where IP = :IP
      into :IP_WHERE;

      if (IP_WHERE is null) then begin
        select first 1
            3
          from BILLING B
          where IP_INET = :IP
        into :IP_WHERE;

        if (IP_WHERE is null) then begin
          select first 1
              4
            from OBJECTS O
            where O.O_TYPE = 20
                  and O.O_NAME = :IP
          into :IP_WHERE;

          if (IP_WHERE is null) then begin
            select first 1
                2
              from ALL_USED_IP I
              where IP = :IP
            into :IP_WHERE;
          end
        end
      end
    end
  end

  return IP_WHERE;
end^



